import React, { Component } from 'react';
import './list.css' ;
import PropTypes from 'prop-types';


class List extends Component{
    constructor(props){
        super(props);
        this.listItems = props.listItems;
        this.listID = props.listID
        this.handleRemoveListItem = this.handleRemoveListItem.bind(this)
        this.handleUpdateListItem = this.handleUpdateListItem.bind(this)


        this.state = {
            isEdit: false
        };
    }

    handleRemoveListItem(id){
        this.props.deleteList(id);

    }

    handleUpdateListItem(id){
        this.setState({ isEdit: true})
        this.props.updateList(id);


    }

    render(props){
        return(
            <>
            {
                 this.state.isEdit
                 ? (
                    <div className="list fade-in">
                    <form onSubmit={this.editResidentsSubmit}>
                             <input placeholder="Name" defaultValue={name} ref={nameInput => this.nameInput = nameInput}/>
                             <input placeholder="Job"  defaultValue={job} ref={jobInput => this.jobInput = jobInput}/>
                             <input placeholder="Study" defaultValue={study} ref={studyInput => this.studyInput = studyInput}/>
                             <button>Save</button>
                     </form>        
                    <div className="close" onClick={() => this.handleRemoveListItem(this.listID)}> 
                    &times;
                    </div> 
                    <div className="update" onClick={() => this.handleUpdateListItem(this.listID)}> 
                    &iexcl;
                    </div> 
                    <p className="listItems">{this.listItems}</p>
                    </div>

                    )
                        : (

                    <div className="list fade-in">
                    <div className="close" onClick={() => this.handleRemoveListItem(this.listID)}> 
                    &times;
                    </div> 
                    <div className="update" onClick={() => this.handleUpdateListItem(this.listID)}> 
                    &iexcl;
                    </div> 
                    <p className="listItems">{this.listItems}</p>
                    </div>
         ) }
         </>
         );
    }
}

List.propTypes = {
      listItems: PropTypes.string
}

export default List;
