import React , { Component }from 'react';
import './App.css';
import List from './components/list'
import NewList from './components/newList'
import { DBfirebaseConfig } from './Config/config'
import firebase from 'firebase/app'

class App extends Component{

  constructor(props){
    super(props);
    this.addList = this.addList.bind(this);

    //fir
    this.app = firebase.initializeApp(DBfirebaseConfig);
    this.db = this.app.database().ref().child("list");


    this.state = {
      list: [
        { id:1 , listItem: "Pole"},
        { id:2, listItem: "Bird"},
      ],
    }
  }

  addList(item){
    //add to list of things I hit with my carx
    const oldList = this.state.list;
    this.state.list.push({id: oldList.length + 1, listItem: item});
    this.setState({
      list : oldList
    })
  }
  render() {
    return (
      <div className="listWrapper">
        <div className="listHeader">
          <h1>Things I've Hit with My Car</h1>
        </div>
          <div className="listForm">
          <NewList addList={this.addList}/>
          </div>
            <div className="listBody">
            {
              this.state.list.map((item) => {
                return(
                  <List listItems={item.listItem} listID={item.id} key={item.id}/>
                )

              })
            }
            </div>
      </div>
    );
  }
}


export default App;
